import * as axios from "axios";
//import authMiddleware from "../../../Backend/Middleware/authMiddleware.js";
import store from "../Redux/reduxStore.js";
import { refreshToken } from "../Redux/Reducers/userReducer.js";
export const API_URL = "http://localhost:3001/api";

const $api = axios.create({
  withCredentials: true,
  baseURL: API_URL,
});

/**
 * Прикрепляем токены к запросам
 */
$api.interceptors.request.use((config) => {
  config.headers.Authorization = "Bearer " + store.getState().user.token;

  return config;
});
/**
 * Перезагрузка токена
 */
$api.interceptors.response.use(
  (config) => {
    return config;
  },
  async (error) => {
    console.log("------error code--------");

    const originalRequest = error.response.config;
    console.log(error.response.config);

    console.log(error.response.config.retry);

    if (
      error.response.status === 401 &&
      error.response.config &&
      (error.response.config.isRetry === false ||
        error.response.config.isRetry === undefined)
    ) {
      originalRequest.isRetry = true;
      console.log("----ебааа мы уже тут ------------");
      console.log(originalRequest);
      try {
        const response = await $api.get("/refresh");
        console.log("----------response-----------------------");
        console.log(response);
        store.dispatch(refreshToken(response.accessToken));
        //store.getState().user.token = response.accessToken;
        return $api.request(originalRequest);
      } catch (error) {
        console.log(error);
      }
    } else {
      throw error;
    }
  }
);
export default $api;
