import CoursesPersonal from "./CoursesPersonal.jsx";
import React from "react";
import { connect } from "react-redux";
import $api from "../../../../Http/index.js";
import { loading } from "../../../../Redux/Reducers/loadingReducer.js";
import { setPersonalCourse } from "../../../../Redux/Reducers/coursesReducer.js";

class coursesPersonalContainer extends React.Component {
  componentDidMount() {
    const currentUrl = window.location.pathname;
    const id = currentUrl.split("/")[2];
    this.props.loading(true);
    const current = {
      personalCourseId: id,
      currentPage: this.props.state.courses.currentPage,
    };
    $api
      .get(
        "/course/personal/?id=" +
          id +
          "&currentPage=" +
          this.props.state.courses.currentPage
      )
      .then((response) => {
        console.log("hi");
        console.log(response);
        this.props.loading(false);
        this.props.setPersonalCourse(response.data);
        console.log("Vse zaebcom");
      })
      .catch((error) => {
        this.props.loading(false);
        console.log("user unauthorized");
        console.log(error);
      });
  }
  render() {
    return (
      <div>
        <CoursesPersonal
          personalCourse={this.props.state.courses.personalCourse}
        />
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    state: state,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    loading: (isLoading) => dispatch(loading(isLoading)),
    setPersonalCourse: (value) => dispatch(setPersonalCourse(value)),
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(coursesPersonalContainer);
