import React from "react";
import { connect } from "react-redux";
import $api from "../../Http/index.js";
import { setUser, getUser, isAuth } from "../../Redux/Reducers/userReducer.js";
import { loading } from "../../Redux/Reducers/loadingReducer.js";
import Login from "./Login.jsx";
import Preloader from "../../Assets/Preloader/PreloaderPage.jsx";
//import { isAuth } from "../../Redux/Reducers/userReducer";
import { Redirect, Route } from "react-router-dom";

class LoginContainer extends React.Component {
  login({ password, email }, props) {
    props.loading(true);
    $api
      .post("/login", { email, password })
      .then((response) => {
        let user = {
          token: response.data.accessToken,
          username: response.data.user.username,
        };
        props.setUser(user);
        props.loading(false);
        props.isAuth(true);
        console.log("Vse zaebcom");
      })
      .catch((error) => {
        props.loading(false);
        console.log("wrong auth");
        console.log(error);
      });
  }
  render() {
    const auth = this.props.state.user.isAuth;
    console.log(auth);
    /*if (auth) {
      return <Redirect to="/courses" />;
    }*/

    return (
      <div>
        {this.props.state.isLoading.isLoading ? (
          <Preloader />
        ) : (
          <Login
            state={this.props.state}
            login={this.login}
            setUser={this.props.setUser}
            loading={this.props.loading}
            isAuth={this.props.isAuth}
          />
        )}
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    state: state,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    setUser: (user) => dispatch(setUser(user)),
    getUser: () => dispatch(getUser()),
    loading: (isLoading) => dispatch(loading(isLoading)),
    isAuth: (value) => dispatch(isAuth(value)),
    //  auth: () => dispatch(auth()),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(LoginContainer);
