const SET_USER = "SET_USER";
const GET_USER = "GET_USER";
const REFRESH_TOKEN = "REFRESH_TOKEN";
const AUTH = "AUTH";
const IS_AUTH = "IS_AUTH";

let initialState = {
  token: "",
  username: "",
  _id: "",
  role: "",
  isAuth: false,
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_USER:
      return {
        ...state,
        token: action.user.token,
        username: action.user.username,
        _id: action.user._id,
        role: action.user.role,
      };
    case GET_USER:
      debugger;
      console.log(state);
      break;
    case IS_AUTH:
      return {
        ...state,
        isAuth: action.value,
      };
    case REFRESH_TOKEN:
      return {
        ...state,
        token: action.value,
      };

    default:
      return state;
  }
};

export default userReducer;

export const setUser = (user) => ({
  type: SET_USER,
  user,
});
export const getUser = () => ({
  type: GET_USER,
});
export const auth = () => ({
  type: AUTH,
});
export const isAuth = (value) => ({
  type: IS_AUTH,
  value,
});
export const refreshToken = (value) => ({
  type: REFRESH_TOKEN,
  value,
});
