import React from "react";
import { connect } from "react-redux";
import $api from "../../Http/index.js";
import { setUser, getUser, loading } from "../../Redux/Reducers/userReducer.js";
import Authentication from "./Authentication.jsx";
import Preloader from "./";

class AuthenticationContainer extends React.Component {
  registration({ username, password, email }, props) {
    try {
      //console.log(this.props);
      props.loading(true);
      console.log(props);
      $api
        .post("/registration", { email, password, username })
        .then((response) => {
          // console.log(response);
          props.state.user.isLoading = false;
          let user = {
            token: response.data.accessToken,
            username: response.data.user.username,
          };
          props.setUser(user);
          props.loading(false);
          // console.log(this.props.state);
        });
    } catch (error) {
      console.log(error);
    }
  }
  render() {
    return (
      <div>
        {console.log(this)}
        {this.props.state.user.isLoading ? (
          <Preloader />
        ) : (
          <Authentication
            state={this.props.state}
            registration={this.registration}
            setUser={this.props.setUser}
            loading={this.props.loading}
          />
        )}
      </div>
    );
  }
}
//console.log(AuthenticationContainer);
const mapStateToProps = (state) => {
  return {
    state: state,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    setUser: (user) => dispatch(setUser(user)),
    getUser: () => dispatch(getUser()),
    loading: (isLoading) => dispatch(loading(isLoading)),
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(AuthenticationContainer);
