import React from "react";
import { connect } from "react-redux";
import $api from "../../../Http/index.js";
import CoursesAdmin from "./CoursesCreate/CoursesAdmin.jsx";
import { loading } from "../../../Redux/Reducers/loadingReducer.js";
import Preloader from "../../../Assets/Preloader/PreloaderPage.jsx";
//import Step from "./CoursesCreate/Step.jsx";
import {
  setCourses,
  addStep,
  createCourse,
} from "../../../Redux/Reducers/coursesReducer.js";
let course = [
  { id: 4, im: 5 },
  { jk: 55, ja: 66 },
];
class CoursesAdminContainer extends React.Component {
  componentDidMount() {
    this.props.loading(true);

    $api
      .get("/course/all")
      .then((response) => {
        console.log(response);
        this.props.loading(false);
        console.log("Vse zaebcom");
      })
      .catch((error) => {
        this.props.loading(false);
        console.log("user unauthorized");
        console.log(error);
      });
  }
  /*createCourse = (courseName) => {
    this.props.loading(true);
    let post = {
      courseName: courseName,
      id: this.props.state.user._id,
    };
    $api
      .post("/admin/course/create", post)
      .then((response) => {
        console.log(response);
        this.props.loading(false);
        this.props.setCourses(response);
        // return <CoursesSteps />;
      })
      .catch((error) => {
        this.props.loading(false);
        console.log("Нет доступа");
        console.log(error);
      });
  };*/
  change = () => {
    this.props.addStep(this.props.state.courses.stepsLength.length);
    console.log(this.props.state);
  };
  createCourse = (obj) => {
    course.map((value, index) => {
      if (value.id === obj.id) {
        course.splice(index, 1, obj);
      }
    });
    console.log(course);
  };

  render() {
    //console.log(this.props.state.courses);
    return (
      <div>
        {this.props.state.isLoading.isLoading ? (
          <Preloader />
        ) : (
          <div>
            <CoursesAdmin
              state={this.props.state}
              loading={this.props.loading}
              change={this.change}
              courses={this.props.state.courses.courses}
              stepsLength={this.props.state.courses.stepsLength}
              createCourse={this.createCourse}
            />
            <button onClick={this.createCourse}></button>
          </div>
        )}
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    state: state,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    loading: (isLoading) => dispatch(loading(isLoading)),
    setCourses: (value) => dispatch(setCourses(value)),
    addStep: (value) => dispatch(addStep(value)),
    createCourse: (value) => dispatch(createCourse(value)),
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(CoursesAdminContainer);
