import style from "./CoursesSteps.module.css";
import React, { useEffect, useState } from "react";
import $api from "../../../../Http";
import Text from "./Types/Text";
import Video from "./Types/Video";
import Img from "./Types/Img";
import Button from "./Types/Button.jsx";
import queryString from "query-string";
import { useHistory } from "react-router-dom";

//
import {
  setCurrentCourseName,
  setCurrentLessonName,
} from "../../../../Redux/Reducers/coursesReducer.js";
import store from "../../../../Redux/reduxStore";
//let initialElement;
function CoursesSteps(props) {
  const [currentStep, setCurrentStep] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [fetching, setFetching] = useState(true);
  const [initialEffect, setInitialEffect] = useState(false);
  const [nextEffect, setNextEffect] = useState(false);
  const [initialElement, setInitialElement] = useState(0);
  const [button, setButton] = useState(false);

  const [initialPath, setInitialPath] = useState(false);
  //const [localLessonName, setLocalLessonName] = useState("")

  const totalCount = props.steps.length;
  let _initialElement = document.getElementById("content");
  const history = useHistory();
  useEffect(() => {
    if (props.currentCourseName && props.currentLessonName) {
      history.push({
        pathname: "/courses/lessons/steps",
        search:
          "?course_name=" +
          props.currentCourseName +
          "&lesson_name=" +
          props.currentLessonName,
      });
    }
  });
  useEffect(() => {
    console.log("jjjjjjjjjjjjjjjjjjjjjj");
    console.log(props.courseName);
    if (!initialPath) {
      const { search } = history.location;
      const parsed = queryString.parse(search);
      console.log("fffffffffffffff");
      console.log(props.initialPath);
      console.log(parsed.course_name);

      store.dispatch(setCurrentCourseName(parsed.course_name));
      store.dispatch(setCurrentLessonName(parsed.lesson_name));
      setInitialPath(true);
    }
  });
  useEffect(() => {
    if (
      _initialElement !== null &&
      _initialElement !== undefined &&
      _initialElement.clientHeight < 1000
    ) {
      setInitialElement(_initialElement.clientHeight);
      setInitialEffect(true);
    }
  }, [initialEffect]);

  useEffect(() => {
    document.addEventListener("scroll", scrollHandler);
    return function () {
      document.removeEventListener("scroll", scrollHandler);
    };
  });

  useEffect(() => {
    $api
      .post("/course/steps", {
        courseName: props.currentCourseName,
        lessonName: props.currentLessonName,
      })
      .then((response) => {
        props.setSteps(response.data.steps);
        setInitialEffect((prevState) => (prevState = true));
      })
      .catch((error) => {
        console.log("user unauthorized");
        console.log(error);
      });
  }, [props.currentLessonNumber, props.currentLessonName]);
  useEffect(() => {
    if (initialEffect) {
      setNextEffect((prevState) => (prevState = true));
      setFetching(true);
    }
  }, [props.currentLessonNumber, initialElement, initialEffect]);
  useEffect(() => {
    if (fetching && nextEffect) {
      $api
        .post("/course/one-step", {
          courseName: props.currentCourseName,
          lessonName: props.currentLessonName,
          stepName: props.steps[currentPage]?.stepName,
        })
        .then((response) => {
          let error = false;
          if (response.data.message) {
            error = true;
          }

          let i = 0;

          currentStep.map((step) => {
            if (step.stepName === response.data?.stepName) {
              i++;
            }
            return step;
          });

          if (i === 0 && !error) {
            setCurrentStep([...currentStep, response.data]);
            setCurrentPage((prevState) => prevState + 1);
          }
        })
        .finally(() => {
          setFetching(false);
          setInitialEffect(false);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, [fetching, nextEffect]);

  const scrollHandler = (e) => {
    if (
      e.target.documentElement.scrollHeight -
        (e.target.documentElement.scrollTop + window.innerHeight) <
        100 &&
      currentStep.length < totalCount
    ) {
      setFetching(true);
    }
  };

  return (
    <div className={style.container}>
      <div id="content">
        {currentStep.map((step, index) => {
          if (step.stepType === "Img") {
            return (
              <Img
                key={index}
                stepName={step.stepName}
                stepContent={step.stepContent}
              />
            );
          }
          if (step.stepType === "Text") {
            return (
              <Text
                key={index}
                stepName={step.stepName}
                stepContent={step.stepContent}
              />
            );
          }
          if (step.stepType === "Video") {
            return (
              <Video
                key={index}
                stepName={step.stepName}
                stepContent={step.stepContent}
              />
            );
          }
          return step;
        })}
        {props.steps.length === currentPage && !button ? (
          <Button
            onPageChanged={props.onPageChanged}
            setFetching={setFetching}
            setCurrentPage={setCurrentPage}
            setCurrentStep={setCurrentStep}
            setButton={() => setButton}
            setNextEffect={setNextEffect}
            setInitialElement={setInitialElement}
            setInitialEffect={setInitialEffect}
          />
        ) : (
          <div></div>
        )}
      </div>
    </div>
  );
}
export default CoursesSteps;
