const SET_COURSES = "SET_COURSES";
const ADD_STEP = "ADD_STEP";
const CREATE_COURSE = "CREATE_COURSE";
const UPDATE_COURSE = "UPDATE_COURSE";

let initialState = {
  courses: [],
  stepsLength: [],
};

const courseReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_COURSES:
      return {
        ...state,
        courses: action.value,
      };
    case ADD_STEP:
      let stepsLength = state.stepsLength;
      stepsLength.push(action.value);
      return {
        ...state,
        stepsLength: stepsLength,
      };
    case CREATE_COURSE:
      return {
        ...state,
        courses: action.value,
      };
    case UPDATE_COURSE:
      console.log("Я тут");
      let array = state.courses;
      if (array.length === 0) {
        array.push(action.value);

        return {
          ...state,
          courses: array,
        };
      } else {
        for (let i = 0; i < array.length; i++) {
          if (array[i].id === action.value.id) {
            array.splice(i, 1);
            return {
              ...state,
              courses: array,
            };
          } else if (i === array.length - 1) {
            array.push(action.value);
            return {
              ...state,
              courses: array,
            };
          }
        }
      }
      return {
        ...state,
        courses: array,
      };
    default:
      return state;
  }
};

export default courseReducer;
export const setCourses = (value) => ({
  type: SET_COURSES,
  value,
});
export const addStep = (value) => ({
  type: ADD_STEP,
  value,
});
export const createCourse = (value) => ({
  type: CREATE_COURSE,
  value,
});
export const updateCourse = (value) => ({
  type: UPDATE_COURSE,
  value,
});
