import style from "./types.module.css";
import { useState } from "react";
function Test(props) {
  const [myQuestion, setMyQuestion] = useState("");
  const [myAnswer, setMyAnswer] = useState("");
  const [selected, setSelected] = useState("");
  const [right, setRight] = useState(false);
  if (!right) {
    props.setBlock(true);
  }
  function testing(props) {
    return function () {
      return (
        <div>
          {props.stepContent.map((content) => {
            return (
              <div className={style.optionContainer}>
                {content.question == myQuestion ? (
                  <div
                    onClick={() => {
                      setSelected("style.selected");
                      setMyAnswer(content.answer);
                      setMyQuestion(content.question);
                    }}
                    className={style.selected}
                  >
                    <div>
                      <div className={style.option}>{content.question}</div>
                    </div>
                  </div>
                ) : (
                  <div
                    onClick={() => {
                      setSelected("style.selected");
                      setMyAnswer(content.answer);
                      setMyQuestion(content.question);
                    }}
                  >
                    <div className={style.option}>{content.question}</div>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      );
    };
  }
  function check() {
    //setRight(false);
    if (myAnswer) {
      setRight(true);
      props.setBlock(false);
      return;
    }
    return;
  }

  return (
    <div className={style.blockContainer}>
      <div>
        <div className={style.blockName}>{props.stepName}</div>
        <div>
          {right ? (
            <div className={style.right}>{testing(props)}</div>
          ) : (
            <div className={style.not}>{testing(props)}</div>
          )}

          <div className={style.checkAnswerContainer}>
            <div
              className={style.checkAnswer}
              onClick={() => {
                check();
              }}
            >
              Check Right Answer
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
export default Test;
