const LOADING = "LOADING";

let initialState = {
  isLoading: false,
};

const loadingReducer = (state = initialState, action) => {
  switch (action.type) {
    case LOADING:
      return {
        ...state,
        isLoading: action.isLoading,
      };
    default:
      return state;
  }
};

export default loadingReducer;

export const loading = (isLoading) => ({
  type: LOADING,
  isLoading,
});
