import style from "../Courses.module.css";
import leftArrow from "../../../../Assets/Arrows/navigate_before_black_24dp.svg";
import rightArrow from "../../../../Assets/Arrows/keyboard_arrow_right_black_24dp.svg";
import Video from "./Types/Video";
import Text from "./Types/Text";
function coursesPersonal(props) {
  /*let pagesArray = [];
  for (let i = 0; i < props.totalPagesCount; i++) {
    pagesArray.push(i);
  }*/
  console.log(props);
  if (props.personalCourse !== "") {
    return (
      <div className={style.container}>
        <div className={style.personalTitle}>{props.courseTitle}</div>
        <div className={style.pagesCount}>
          {props.steps.map((p) => (
            <div className={style.page}>
              <span
                className={style.pageBack}
                onClick={() => props.onPageChanged(p.stepName)}
              >
                <div className={style.unselectedPage}>
                  <div
                    className={props.currentPage === p && style.selectedPage}
                  >
                    {props.personalProgress[p] === true ? (
                      <div className={style.passedPage}></div>
                    ) : (
                      <div className={style.unpassedPage}></div>
                    )}
                  </div>
                </div>
              </span>
            </div>
          ))}
        </div>
        <div className={style.contentContainer}>
          <div
            className={style.arrow}
            onClick={() => {
              if (props.currentPage !== 0) {
                props.onPageChanged(props.currentPage - 1);
              }
            }}
          >
            <div></div>
            <img width={40} className={style.imgArrow} src={leftArrow} alt="" />
            <div></div>
          </div>
          <div>
            {props.personalCourse.stepType === "Video" && (
              <Video
                stepTitle={props.personalCourse.stepTitle}
                stepContent={props.personalCourse.stepContent}
              />
            )}
            {props.personalCourse.stepType === "Text" && (
              <Text
                stepTitle={props.personalCourse.stepTitle}
                stepContent={props.personalCourse.stepContent}
              />
            )}
          </div>
          <div
            className={style.arrow}
            onClick={() => {
              if (props.currentPage !== props.totalPagesCount - 1) {
                props.onPageChanged(props.currentPage + 1);
              }
            }}
          >
            <div></div>
            <img
              className={style.imgArrow}
              width={40}
              src={rightArrow}
              alt=""
            />
            <div></div>
          </div>
        </div>
      </div>
    );
  }
  return <div>Sorry, but smth went wrong</div>;
}
export default coursesPersonal;
