import CoursesPersonal from "./CoursesPersonal.jsx";
import React from "react";
import { connect } from "react-redux";
import $api from "../../../../Http/index.js";
import { loading } from "../../../../Redux/Reducers/loadingReducer.js";
import {
  setPersonalCourse,
  setTotalPagesCount,
  setCourseId,
  setCurrentPage,
  setCourseTitle,
  setPersonalProgress,
} from "../../../../Redux/Reducers/coursesReducer.js";

class coursesPersonalContainer extends React.Component {
  componentDidMount() {
    $api
      .post("/course/steps", {
        courseName: this.props.courses.currentCourseName,
        lessonName: this.props.courses.currentLessonName,
      })
      .then((response) => {
        this.props.loading(false);
        console.log(response);
        this.props.setPersonalCourse(response.data.step);
        this.props.setTotalPagesCount(response.data.courseLength);
        this.props.setCourseTitle(response.data.courseTitle);
        $api
          .post("/course/personal/getPropgress", {
            courseId: this.props.state.courses.courseId,
          })
          .then((response) => {
            this.props.setPersonalProgress(response.data);
          })
          .catch((error) => {
            this.props.loading(false);
            console.log("user unauthorized");
            console.log(error);
          });
      })
      .catch((error) => {
        this.props.loading(false);
        console.log("user unauthorized");
        console.log(error);
      });
  }
  onPageChanged = (p) => {
    let currentPage = p;
    $api
      .post("/course/personal", {
        id: this.props.state.courses.courseId,
        currentPage,
      })
      .then((response) => {
        this.props.loading(false);
        this.props.setPersonalCourse(response.data.step);
        this.props.setTotalPagesCount(response.data.courseLength);
        this.props.setCurrentPage(currentPage);
        this.props.setCourseTitle(response.data.courseTitle);
      })
      .catch((error) => {
        this.props.loading(false);
        console.log("user unauthorized");
        console.log(error);
      });
    $api
      .post("/course/personal/postPropgress", {
        courseId: this.props.state.courses.courseId,
        stepNumber: p,
      })
      .then((response) => {
        this.props.setPersonalProgress(response.data.steps);
      })
      .catch((error) => {
        this.props.loading(false);
        console.log("user unauthorized");
        console.log(error);
      });
  };
  render() {
    return (
      <div>
        <CoursesPersonal
          courseTitle={this.props.state.courses.courseTitle}
          personalCourse={this.props.state.courses.personalCourse}
          totalPagesCount={this.props.state.courses.totalPagesCount}
          currentPage={this.props.state.courses.currentPage}
          onPageChanged={this.onPageChanged}
          personalProgress={this.props.state.courses.personalProgress}
        />
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    state: state,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    loading: (isLoading) => dispatch(loading(isLoading)),
    setPersonalCourse: (value) => dispatch(setPersonalCourse(value)),
    setTotalPagesCount: (value) => dispatch(setTotalPagesCount(value)),
    setCourseId: (value) => dispatch(setCourseId(value)),
    setCurrentPage: (value) => dispatch(setCurrentPage(value)),
    setCourseTitle: (value) => dispatch(setCourseTitle(value)),
    setPersonalProgress: (value) => dispatch(setPersonalProgress(value)),
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(coursesPersonalContainer);
