import React from "react";
import { connect } from "react-redux";
import $api from "../../Http/index.js";
import { setUser, getUser } from "../../Redux/Reducers/userReducer.js";
import { loading } from "../../Redux/Reducers/loadingReducer.js";
import Authentication from "./Authentication.jsx";
import Preloader from "../../Assets/Preloader/PreloaderPage.jsx";

class AuthenticationContainer extends React.Component {
  registration({ username, password, email }, props) {
    props.loading(true);
    $api
      .post("/registration", { email, password, username })
      .then((response) => {
        /* let user = {
            token: response.data.accessToken,
            username: response.data.user.username,
          };
          props.setUser(user);*/
        props.loading(false);
      });
  }
  render() {
    return (
      <div>
        {this.props.state.isLoading.isLoading ? (
          <Preloader />
        ) : (
          <Authentication
            state={this.props.state}
            registration={this.registration}
            loading={this.props.loading}
          />
        )}
      </div>
    );
  }
}
//console.log(AuthenticationContainer);
const mapStateToProps = (state) => {
  return {
    state: state,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    setUser: (user) => dispatch(setUser(user)),
    getUser: () => dispatch(getUser()),
    loading: (isLoading) => dispatch(loading(isLoading)),
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(AuthenticationContainer);
