import React from "react";
import { connect } from "react-redux";
import $api from "../../Http/index.js";
import { setUser, getUser } from "../../Redux/Reducers/userReducer.js";
import Authentication from "./Authentication.jsx";
import Preloader from "../../Assets/Preloader/Rocket.gif";

class AuthenticationContainer extends React.Component {
  registration({ username, password, email }) {
    try {
      console.log(this);
      this.state.user.isLoading = true;
      $api
        .post("/registration", { email, password, username })
        .then((response) => {
          console.log(response);
          let user = {
            token: response.data.accessToken,
            username: response.data.user.username,
          };
          this.setUser(user);
          console.log(this.state);
        });
    } catch (error) {
      console.log(error);
    }
  }
  render() {
    return (
      <div>
        {this.state.user.isLoading ? (
          <Preloader />
        ) : (
          <Authentication
            state={this.props.state}
            registration={this.registration}
            setUser={this.props.setUser}
          />
        )}
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    state: state,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    setUser: (user) => dispatch(setUser(user)),
    getUser: () => dispatch(getUser()),
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(AuthenticationContainer);
