import style from "./Courses.module.css";
import React from "react";
import { useState } from "react";
import Step from "./Step.jsx";
import CourseTitle from "./SubComponents/CourseTitle";

function CoursesAdmin(props, step) {
  const [courseName, setCourseName] = useState("");
  const [stepTitle, setStepTitle] = useState("");
  const [stepType, setStepType] = useState("");
  const [stepContent, setStepContent] = useState("");
  if (props.state.courses.courses.length === 0) {
    return (
      <div className={style.container}>
        <div>
          <div>Courses page is empty</div>
          <div className={style.courseCreator}>
            <CourseTitle
              courseName={courseName}
              setCourseName={setCourseName}
            />
          </div>
          <div>
            {props.stepsLength.map((p) => (
              <div name={p.toString()} key={p.toString()}>
                <Step
                  stepTitle={stepTitle}
                  setStepTitle={setStepTitle}
                  stepType={stepType}
                  setStepType={setStepType}
                  stepContent={stepContent}
                  setStepContent={setStepContent}
                  courses={props.courses}
                  id={p}
                />
              </div>
            ))}
          </div>
          <button onClick={props.change}>Add Step</button>
          <button onClick={props.createCourse}></button>
        </div>
      </div>
    );
  }
}
export default CoursesAdmin;
