import StepContent from "./SubComponents/StepContent.jsx";
import StepType from "./SubComponents/StepType.jsx";
import StepTitle from "./SubComponents/StepTitle.jsx";
import style from "./Courses.module.css";

import { useState } from "react";

function Step(props) {
  let step = [];
  const [stepTitle, setStepTitle] = useState("");
  const [stepType, setStepType] = useState("");
  const [stepContent, setStepContent] = useState("");
  /*const step = {
    stepTitle,
    stepType,
    stepContent,
  };*/
  let obj = { stepTitle, stepType, stepContent, id: props.id };
  step.shift();
  step.push(obj);

  //// Условие
  /* let i = 0;
  props.courses.map((p) => {
    if (p === obj) {
      i++;
    }
    if (i > 1) {
    }
    if (i === 0) {
      props.courses.push(step);
    }
  });
*/
  console.log(step);

  return (
    <div className={style.steps}>
      <div>Steps:</div>
      <div className={style.oneStep}>
        <div className={style.stepTitle}>
          <div>Title</div>
          <input
            value={stepTitle}
            onInput={(evt) => setStepTitle(evt.target.value)}
            type="text"
            placeholder="Step Title"
          />
        </div>
        <div>
          <div>Type</div>
          <div className={style.type}>
            <select
              defaultValue={""}
              value={stepType}
              onChange={(evt) => setStepType(evt.target.value)}
            >
              <option>Text</option>
              <option>Video</option>
              <option>Test</option>
            </select>
          </div>
        </div>
        <div>
          <div>Content: </div>
          <div>
            <textarea
              value={stepContent}
              onChange={(evt) => setStepContent(evt.target.value)}
            ></textarea>
          </div>
        </div>
      </div>
    </div>
  );
}
export default Step;
