//const SET_ROLE = "SET_ROLE";
const SET_USER = "SET_USER";

let initialState = {
  token: "",
  username: "",
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_USER:
      return {
        ...state,
        token: action.token,
        username: action.username,
      };
    default:
      return state;
  }
};

export default userReducer;

export const setUserAC = (user) => ({
  type: SET_USER,
  user,
});
