const SET_COURSES = "SET_COURSES";
const ADD_STEP = "ADD_STEP";
const CREATE_COURSE = "CREATE_COURSE";

let initialState = {
  courses: [{ id: 0 }],
  steps: [],
};

const courseReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_COURSES:
      return {
        ...state,
        courses: action.value,
      };
    case ADD_STEP:
      return {
        ...state,
        steps: action.value,
      };
    case CREATE_COURSE:
      let courses = state.courses.filter(function (p) {
        return p !== action.value;
      });
      return {
        ...state,
        courses: courses,
      };
    default:
      return state;
  }
};

export default courseReducer;
export const setCourses = (value) => ({
  type: SET_COURSES,
  value,
});
export const addStep = (value) => ({
  type: ADD_STEP,
  value,
});
export const createCourse = (value) => ({
  type: CREATE_COURSE,
  value,
});
