import style from "../Courses.module.css";
import rightArrow from "../../../../Assets/Arrows/arrow_right_alt_black_24dp.svg";
import Video from "./Types/Video";
import Text from "./Types/Text";
function coursesPersonal(props) {
  let pagesArray = [];
  for (let i = 0; i < props.totalPagesCount; i++) {
    pagesArray.push(i);
  }

  if (props.personalCourse !== "") {
    return (
      <div className={style.container}>
        <div className={style.personalTitle}>{props.courseTitle}</div>
        <div className={style.pagesCount}>
          {pagesArray.map((p) => (
            <div className={style.page}>
              <span
                className={style.pageBack}
                onClick={() => props.onPageChanged(p)}
              >
                <div className={props.currentPage === p && style.selectedPage}>
                  {p}
                </div>
              </span>
            </div>
          ))}
        </div>
        <div>
          <div>
            <button>
              <img className={style.leftArrow} src={rightArrow} alt="" />
            </button>
          </div>
          <div>
            {props.personalCourse.stepType === "Video" && (
              <Video
                stepTitle={props.personalCourse.stepTitle}
                stepContent={props.personalCourse.stepContent}
              />
            )}
            {props.personalCourse.stepType === "Text" && (
              <Text
                stepTitle={props.personalCourse.stepTitle}
                stepContent={props.personalCourse.stepContent}
              />
            )}
          </div>
          <div>
            <button>
              <img className={style.rightArrow} src={rightArrow} alt="" />
            </button>
          </div>
        </div>
      </div>
    );
  }
  return <div>Sorry, but smth went wrong</div>;
}
export default coursesPersonal;
