import style from "./CoursesSteps.module.css";
import React, { useEffect, useState } from "react";
import $api from "../../../../Http";
import Text from "./Types/Text";
import Video from "./Types/Video";
import Img from "./Types/Img";
import Button from "./Types/Button.jsx";
import queryString, { parse } from "query-string";
//import queryString from "query-string";

import { useHistory } from "react-router-dom";
import changeStepsArray from "./setSteps.js";
//const queryString = require("query-string");
//

//let initialElement;
function CoursesSteps(props) {
  const [currentStep, setCurrentStep] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  //const [fetching, setFetching] = useState(true);
  const [initialEffect, setInitialEffect] = useState(true);
  const [changes, setChanges] = useState(false);
  // const [nextEffect, setNextEffect] = useState(false);
  //const [initialElement, setInitialElement] = useState(0);
  // const [_initialElement, _setInitialElement] = useState(0);
  const [button, setButton] = useState(false);

  const [initialPath, setInitialPath] = useState(true);
  //const [localLessonName, setLocalLessonName] = useState("")

  const totalCount = props.steps.length;
  /* useEffect(() => {
    _setInitialElement(document.getElementById("content"));
  });*/

  const history = useHistory();

  /* useEffect(() => {
    if (props.lessons) {
      props.lessons.map((lesson, index) => {
        if (lesson.lessonName == props.currentLessonName) {
          props.setCurrentLessonNumber(index);
        }
      });
    }
  });*/
  /**
   * СБРОС ВСЕХ ЭФФЕКТОВ ПРИ ИЗМЕНЕНИИ CURRENT LESSON NAME
   */

  /**
   * Самый первый эффект при загрузке страницы
   */
  useEffect(() => {
    console.log(history);

    if (initialPath && !initialEffect) {
      console.log("initial effect");

      const parsed = queryString.parse(history.location.search);
      $api
        .post("/course/lessons", { courseName: parsed.course_name })
        .then((response) => {
          console.log(response);
          props.setLessons(response.data.lessons);
        });
      props.setCurrentCourseName(parsed.course_name);
      props.setCurrentLessonName(parsed.lesson_name);

      if (
        parsed.course_name !== "" &&
        parsed.lesson_name !== "" &&
        props.auth
      ) {
        changeStepsArray(parsed.course_name, parsed.lesson_name);
        setInitialPath(false);
      }
    }
  }, [props.auth, initialEffect]);

  useEffect(() => {
    console.log(initialEffect);
    if (props.currentCourseName && props.currentLessonName) {
      history.push({
        pathname: "/courses/lessons/steps",
        search:
          "?course_name=" +
          props.currentCourseName +
          "&lesson_name=" +
          props.currentLessonName,
      });
      setCurrentStep([]);
      setCurrentPage(0);
      setInitialPath(true);
    }
    setInitialEffect(false);
    console.log(initialEffect);
    if (props.currentCourseName && props.currentLessonName) {
      const parsed = queryString.parse(history.location.search);
      if (
        parsed.course_name !== props.currentCourseName ||
        parsed.lesson_name !== props.currentLessonName
      ) {
        history.push({
          pathname: "/courses/lessons/steps",
          search:
            "?course_name=" +
            props.currentCourseName +
            "&lesson_name=" +
            props.currentLessonName,
        });
        setChanges((prevState) => {
          switch (prevState) {
            case false:
              prevState = true;
              break;
            case true:
              prevState = false;
              break;
            default:
              break;
          }
        });
        setCurrentStep([]);
        setCurrentPage(0);
        setInitialPath(true);
      } else {
        history.push({
          pathname: "/courses/lessons/steps",
          search:
            "?course_name=" +
            props.currentCourseName +
            "&lesson_name=" +
            props.currentLessonName,
        });
      }
    }
  }, [props.auth, props.currentLessonName]);

  /*useEffect(() => {
    console.log(currentStep);
    console.log(initialElement);
    if (
      document.getElementById("content") !== null &&
      document.getElementById("content") !== undefined &&
      document.getElementById("content").clientHeight < 1000 &&
      !initialPath
    ) {
      console.log("popal");
      setInitialElement(document.getElementById("content").clientHeight);
      setNextEffect(true);
      setFetching(true);
    }
  });*/
  //[initialPath, initialElement]
  //[initialPath, currentStep]
  /* useEffect(() => {
    document.addEventListener("scroll", scrollHandler);
    return function () {
      document.removeEventListener("scroll", scrollHandler);
    };
  });*/

  /*useEffect(() => {
    setNextEffect((prevState) => (prevState = true));
    setFetching(true);
  }, [props.currentLessonNumber, initialElement, initialEffect, initialPath]);*/
  useEffect(() => {
    if (props.steps.length > 0 && currentStep.length < totalCount) {
      /*  console.log("course-name: " + props.currentCourseName);
      console.log("lesson-name: " + props.currentLessonName);
      console.log("step-name: " + props.steps[currentPage]?.stepName);*/
      const { search } = history.location;
      const parsed = queryString.parse(search);

      $api
        .post("/course/one-step", {
          courseName: parsed.course_name,
          lessonName: parsed.lesson_name,
          stepName: props.steps[currentPage]?.stepName,
        })
        .then((response) => {
          let error = false;
          if (response.data.message) {
            error = true;
          }

          let i = 0;

          currentStep.map((step) => {
            if (step.stepName === response.data?.stepName) {
              i++;
            }
            return step;
          });

          if (i === 0 && !error) {
            setCurrentStep([...currentStep, response.data]);
            setCurrentPage((prevState) => prevState + 1);
          }
        })
        .finally(() => {
          // setFetching(false);
          //setInitialEffect(false);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  });

  /*const scrollHandler = (e) => {
    if (
      e.target.documentElement.scrollHeight -
        (e.target.documentElement.scrollTop + window.innerHeight) <
        100 &&
      currentStep.length < totalCount
    ) {
      setFetching(true);
    }
  };*/

  return (
    <div className={style.container}>
      <div id="content">
        {currentStep.map((step, index) => {
          if (step.stepType === "Img") {
            return (
              <Img
                key={index}
                stepName={step.stepName}
                stepContent={step.stepContent}
              />
            );
          }
          if (step.stepType === "Text") {
            return (
              <Text
                key={index}
                stepName={step.stepName}
                stepContent={step.stepContent}
              />
            );
          }
          if (step.stepType === "Video") {
            return (
              <Video
                key={index}
                stepName={step.stepName}
                stepContent={step.stepContent}
              />
            );
          }
          return step;
        })}
        {totalCount !== 0 &&
        (totalCount === currentPage || totalCount < currentPage) &&
        !button ? (
          <Button
            onPageChanged={props.onPageChanged}
            setCurrentPage={setCurrentPage}
            setCurrentStep={setCurrentStep}
            setButton={() => setButton}
            setInitialEffect={setInitialEffect}
            setInitialPath={setInitialPath}
          />
        ) : (
          <div></div>
        )}
      </div>
    </div>
  );
}
export default CoursesSteps;
