import CoursesPersonal from "./CoursesPersonal.jsx";
import React from "react";
import { connect } from "react-redux";
import $api from "../../../../Http/index.js";
import { loading } from "../../../../Redux/Reducers/loadingReducer.js";
import {
  setCurrentStepName,
  setSteps,
  setPersonalCourse,
} from "../../../../Redux/Reducers/coursesReducer.js";

class coursesPersonalContainer extends React.Component {
  componentDidMount() {
    console.log(this.props);
    $api
      .post("/course/steps", {
        courseName: this.props.state.courses.currentCourseName,
        lessonName: this.props.state.courses.currentLessonName,
      })
      .then((response) => {
        console.log(response);
        this.props.loading(false);
        this.props.setSteps(response.data.steps.stepName);
        this.props.setCurrentStepName(response.data.steps[0]);
        console.log(this.props.state.courses);
        $api
          .post("/course/one-step", {
            courseName: this.props.state.courses.currentCourseName,
            lessonName: this.props.state.courses.currentLessonName,
            stepName: this.props.state.courses.currentStepName,
          })
          .then((response) => {
            console.log(response);
            this.props.setPersonalCourse(response.data);
          });
        //this.props.setCourseTitle(response.data.courseTitle);
      })
      .catch((error) => {
        this.props.loading(false);
        console.log("user unauthorized");
        console.log(error);
      });
  }
  /*onPageChanged = (p) => {
    let currentPage = p;
    $api
      .post("/course/personal", {
        id: this.props.state.courses.courseId,
        currentPage,
      })
      .then((response) => {
        this.props.loading(false);
        this.props.setPersonalCourse(response.data.step);
        this.props.setTotalPagesCount(response.data.courseLength);
        this.props.setCurrentPage(currentPage);
        this.props.setCourseTitle(response.data.courseTitle);
      })
      .catch((error) => {
        this.props.loading(false);
        console.log("user unauthorized");
        console.log(error);
      });
    $api
      .post("/course/personal/postPropgress", {
        courseId: this.props.state.courses.courseId,
        stepNumber: p,
      })
      .then((response) => {
        this.props.setPersonalProgress(response.data.steps);
      })
      .catch((error) => {
        this.props.loading(false);
        console.log("user unauthorized");
        console.log(error);
      });
  };*/
  render() {
    return (
      <div>
        <CoursesPersonal steps={this.props.state.courses.steps} />
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    state: state,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    loading: (isLoading) => dispatch(loading(isLoading)),
    setPersonalCourse: (value) => dispatch(setPersonalCourse(value)),
    setCurrentStepName: (value) => dispatch(setCurrentStepName(value)),
    setSteps: (value) => dispatch(setSteps(value)),
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(coursesPersonalContainer);
